INSERT INTO `soft_center` VALUES ('2', '实验中心简介', '<p><span style=\"font-family: 微软雅黑,Microsoft YaHei;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 郑州轻工业学院软件综合实践与创新基地是隶属于郑州轻工学院工程训练中心的一个以软件学科教学辅助与软件储备人才培养为主要任务的创新型、实践型的校级软件学科实验室。下设郑轻软创团队和软件学科新生集训队。</span></p><p><span style=\"font-family: 微软雅黑,Microsoft YaHei;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在过去的一年中团队曾代表学校参加软件学科各类竞赛，为学校和学院取得诸多荣誉。其中省级奖项三十余项，国级奖项五项，为学校开发和投用多套OA系统，在本校软件方面具有较大影响力。</span></p><p><span style=\"font-family: 微软雅黑,Microsoft YaHei;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n				近两年，出自本实验室的毕业生，多被京东、群硕等知名公司录取。</span></p><p><br/></p>', '2017-03-17 00:00:00', '1', '42', 'null');
